// src/openaiService.js

const API_URL = 'https://api.openai.com/v1/engines/davinci/completions'; // Update to the appropriate URL for the model you are using

const openaiService = async (prompt) => {
    const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer sk-proj-shUOzy7qRiqdiezgetdPT3BlbkFJqxRa36AqPXbi49Fu8lMD`, // Replace with your OpenAI API key
        },
        body: JSON.stringify({
            prompt: prompt,
            max_tokens: 150,
            temperature: 0.7,
        }),
    });

    const data = await response.json();
    return data.choices[0].text;
};

export default openaiService;
