import React, { useState } from 'react';
import Sidebar from './Sidebar';
import HomePage from './HomePage';
import ChatInterface from './ChatInterface';
import './App.css';
import StartPage from './StartPage';

const App = () => {
    const [page, setPage] = useState('start');

    const handleNavigate = (page) => {
        setPage(page);
    }

    const [currentSkill, setCurrentSkill] = useState('active listening');
    const [scenarios, setScenarios] = useState([currentSkill.scenarios]);


    const handleSelectSkill = (skill) => {
        setCurrentSkill(skill);
    }

    const handleSelectScenario = (skill) => {
        setScenarios(skill.scenarios);
    }

    const handleStartNewSession = () => {
        setPage('chat1');
    }

    // Define pages where sidebar should be hidden
    const pagesWithHiddenSidebar = ['start'];

    return (
        <div className="app">
            {!pagesWithHiddenSidebar.includes(page) && <Sidebar items={scenarios} onNavigate={handleNavigate} />}
            <div className="content">
                {page === 'start' && <StartPage onNavigate={handleNavigate} onSelectSkill={handleSelectSkill} onSelectScenario={handleSelectScenario}/>}
                {page === 'home' && <HomePage scenarios={scenarios} onStartNewSession={handleStartNewSession} currentSkill={currentSkill} onNavigate={handleNavigate} />}                
                {scenarios.length > 0 && scenarios.map((scenario, index) => (
                    page === `chat${index + 1}` && <ChatInterface onNavigate={handleNavigate} key={index} currentSkill={currentSkill} />
                ))}
            </div>
        </div>
    );
}

export default App;
