import React, { useState } from 'react';
import Sidebar from './Sidebar';
import HomePage from './HomePage';
import ChatInterface from './ChatInterface';
import './App.css';

const App = () => {
    const [page, setPage] = useState('home');

    const handleNavigate = (page) => {
        setPage(page);
    }

    const currentSkill = "active listening";
    const scenarios = ["scenario1"];


    const handleStartNewSession = () => {
        setPage('chat1');
        // insert code here to get more information from the code about the session
    }


    return (
        <div className="app">
            <div className="sidebar-item" onClick={() => onNavigate('home')}>üè†</div>
            <div className="content">
                {page === 'home' && <HomePage onStartNewSession={handleStartNewSession} />}
                {scenarios.length > 0 && scenarios.map((scenario, index) => (
                    page === `chat${index + 1}` && <ChatInterface key={index} currentSkill={currentSkill} />
                ))}
            </div>
        </div>
    );
}

export default App;
